import React, {useEffect,useState} from "react"
import {useNavigate}from "react-router-dom"

import axios from "axios"

function MyBook(){
//For my bookForm 
const navigate=useNavigate()
const[goToForm,setGoToForm]= useState(false)
//state setup
const [Form,setForm]=useState([{
    name:"",
    ingredents:"",
    prep:"",
    cook:""
  }])

  useEffect(()=>{
    fetch("/Mybook").then(res=>{
        if(res.ok){
            return res.json()
        }
    }).then(jsonRes=> setForm(jsonRes))

    })
 


if(goToForm){
    navigate("/MyBookForm")
    
  }
const handleClick = (e) => {
    e.preventDefault()  
    setGoToForm(true)
}
 const ShowData=()=>{
axios
        .get("http://localhost:5000/api",Form)
         .then((res)=>console.log(res))
         .catch((err)=> console.log(err))   
         return(
            <div>
                <h1>{}</h1>
            </div>
         )   
}


return(

    <div className="MyBook">
        
        <h1>MyBook</h1>
        <button onClick={handleClick} variant="primary" type="submit">
            Add My Recipe
            </button>
        <div>
        <ShowData/>
        {Form.map(resp=>
        <div>
            <h1>{resp.name}</h1>
            <p>{resp.ingredents}</p>
            <p>{resp.prep}</p>
            <p>{resp.cook}</p>
            </div>
            )}
            
        </div>
    </div>
    
    
)
}
export default MyBook;
